23; console.log(ret); var item=ret[i], eleTop=item.offsetTop, eleEnd=eleTop+item.offsetHeight; if((eleTop&gt;winTop&amp;&amp;eleTop&lt;=winEnd)||(eleEnd&gt;winTop&amp;&amp;eleEnd&lt;=winEnd))&#123; $(item).css('background','none'); new Tab($(item).attr('id'),data).init; delete ret[i]; &#125; &#125; &#125; return &#123; timer:timer; isExist:isExist; &#125;;&#125;)($);//第三种方法Zepto(function ($) &#123; var swiper = new Swiper('.swiper-container', &#123; pagination: '.swiper-pagination', paginationClickable: true, autoplay: 3000, loop: true, autoplayDisableOnInteraction: false &#125;); (function lazyLoad() &#123; var imgs = $(".lazyLoad"); var src = ''; $.each(imgs, function (index, item) &#123; src = $(item).attr('data-src'); $(item).attr('src', src); &#125;); &#125;)();&#125;);$(function () &#123; var lazyLoadTimerId = null; /// 智能加载事件 $(window).bind("scroll", function () &#123; clearTimeout(lazyLoadTimerId); lazyLoadTimerId = setTimeout(function () &#123; // 延迟加载所有图片 var isHttp = (location.protocol === "http:"); $("#ym_images img").each(function () &#123; var self = $(this); if (self.filter(":above-the-fold").length &gt; 0) &#123; var originUrl = self.attr("data-original"); self.attr("src", originUrl); &#125; &#125;); &#125;, 500); &#125;);&#125;); 某年某月的1号为星期几 12var weekday = ["星期日", "星期一", "星期二", "星期三", "星期四", "星期五", "星期六"];weekday[new Date(2015, 9, 1).getDay()]; //2015年10月1号 2. Mobile - js js 判断IOS, 安卓12345var u = navigator.userAgent, app = navigator.appVersion;var isAndroid = u.indexOf('Android') &gt; -1 || u.indexOf('Linux') &gt; -1; //android终端或者uc浏览器var isiOS = !!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/); //ios终端alert('是否是Android：'+isAndroid);alert('是否是iOS：'+isiOS); 3. 微信 weixin UserAgent 判断微信客户端 12345// Mozilla/5.0 (iPhone; CPU iPhone OS 8_3 like Mac OS X) AppleWebKit/600.1.4 (KHTML, like Gecko) Mobile/12F70 MicroMessenger/6.1.5 NetType/WIFIfunction isWechat() &#123; var ua = navigator.userAgent.toLowerCase(); return /micromessenger/i.test(ua) || /windows phone/i.test(ua);&#125; JS接口安全域名不填写，分享onMenuShareAppMessage直接会取默认值。 1// 分享onMenuShareAppMessage直接会取默认值 关闭当前页面 1WeixinJSBridge.call('closeWindow'); 支付接口方法调用必须在addevent里边调用 123document.addEventListener('WeixinJSBridgeReady', function onBridgeReady()&#123; that.initOrder();&#125;, false); 支付接口方法调用必须在 123456789101112WeixinJSBridge.invoke('getBrandWCPayRequest', d, function(res)&#123; if(res.err_msg == "get_brand_wcpay_request:ok")&#123; // alert("支付成功"); // union.release(d.orderId); resetUrl(); paySuccess('home', d.orderId); &#125; else &#123; cancelOrder(d.orderId); // alert(res.err_msg); &#125; loading.hide();&#125;); 瀑布流无限加载实例 12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061// be dependent on jquery &amp; jquery.infinitescroll.min.js// insert this '&lt;div id="more"&gt;&lt;a href="api?page="&gt;&lt;/a&gt;&lt;/div&gt;' to your page.html(function($)&#123; $(function()&#123; var $container = $('.list-wrap-gd'); function layOutCallBack() &#123; $container.imagesLoaded(function()&#123; $container.masonry(&#123; itemSelector: '.item-bar', gutter: 10 &#125;); &#125;); $container.imagesLoaded().progress( function() &#123; $container.masonry('layout'); &#125;); &#125; layOutCallBack(); $container.infinitescroll(&#123; navSelector : "#more", nextSelector : "#more a", itemSelector : ".item-bar", pixelsFromNavToBottom: 300, loading:&#123; img: "/images/masonry_loading.gif", msgText: ' ', finishedMsg: "&lt;em&gt;已经到最后一页&lt;/em&gt;", finished: function()&#123; $("#more").remove(); $("#infscr-loading").hide(); &#125; &#125;, errorCallback:function()&#123; $(window).unbind('.infscr'); &#125;, pathParse: function (path, nextPage) &#123; var query = ""; var keyword=$("#search_keyword").val(); var cat_id=$("#cat_id").val(); var brand_id=$("#brand_id").val(); var country_id = $("#country_id").val(); query = query + "&amp;namekeyword="+keyword; query = query +"&amp;cat_id="+cat_id query = query + "&amp;brand_id=" + brand_id; query = query + "&amp;country_id=" + country_id; path = [path,query]; return path; &#125; &#125;, function(newElements) &#123; var $newElems = $( newElements ).css(&#123; opacity: 0 &#125;); $newElems.imagesLoaded(function()&#123; $newElems.animate(&#123; opacity: 1 &#125;); $container.masonry( 'appended', $newElems, true ); layOutCallBack(); &#125;); &#125;); &#125;);&#125;)(jQuery); iOS，Safari浏览器，input等表单focus后fixed元素错位问题123456789101112131415if( /iPhone|iPod|iPad/i.test(navigator.userAgent) ) &#123; $(document).on('focus', 'input, textarea', function() &#123; $('header').css("position", 'absolute'); $('footer').css("position", 'absolute'); &#125;); $(document).on('blur', 'input, textarea', function() &#123; $('header').css("position", 'fixed'); $('footer').css("position", 'fixed'); &#125;);&#125; 得到地理位置 123456789101112function getLocation(callback)&#123; if(navigator.geolocation)&#123; navigator.geolocation.getCurrentPosition( function(p)&#123; callback(p.coords.latitude, p.coords.longitude); &#125;, function(e)&#123; var msg = e.code + "
" + e.message; &#125; ); &#125;&#125; rem计算适配 12345678910111213(function(doc, win)&#123; var docEl = doc.documentElement, resizeEvt = 'orientationchange' in window ? 'orientationchange' : 'resize', recalc = function()&#123; var clientWidth = docEl.clientWidth; if(!clientWidth) return; docEl.style.fontSize = 20 * (clientWidth / 320) + 'px'; &#125;; if(!doc.addEventListener) return; win.addEventListener(resizeEvt, recalc, false); doc.addEventListener('DOMContentLoaded', recalc, false);&#125;)(document, window); 另外一种rem方案 1234567891011121314151617181920212223242526272829303132var dpr, rem, scale;var docEl = document.documentElement;var fontEl = document.createElement('style');var metaEl = document.querySelector('meta[name="viewport"]');dpr = window.devicePixelRatio || 1;rem = docEl.clientWidth * 2 / 10;scale = 1 / dpr;// 设置viewport，进行缩放，达到高清效果metaEl.setAttribute('content', 'width=' + dpr * docEl.clientWidth + ',initial-scale=' + scale + ',maximum-scale=' + scale + ', minimum-scale=' + scale + ',user-scalable=no');// 设置data-dpr属性，留作的css hack之用docEl.setAttribute('data-dpr', dpr);// 动态写入样式docEl.firstElementChild.appendChild(fontEl);fontEl.innerHTML = 'html&#123;font-size:' + rem + 'px!important;&#125;';// 给js调用的，某一dpr下rem和px之间的转换函数window.rem2px = function(v) &#123; v = parseFloat(v); return v * rem;&#125;;window.px2rem = function(v) &#123; v = parseFloat(v); return v / rem;&#125;;window.dpr = dpr;window.rem = rem; 获取js所在路径 12345678910111213function getJsDir (src) &#123; var script = null; if (src) &#123; script = [].filter.call(document.scripts, function (v) &#123; return v.src.indexOf(src) !== -1; &#125;)[0]; &#125; else &#123; script = document.scripts[document.scripts.length - 1]; &#125; return script ? script.src.substr(0, script.src.lastIndexOf('/')) : script;&#125; 从全局捕获错误 12345678910111213window.onerror = function (errMsg, scriptURI, lineNumber, columnNumber, errorObj) &#123; setTimeout(function () &#123; var rst = &#123; "错误信息：": errMsg, "出错文件：": scriptURI, "出错行号：": lineNumber, "出错列号：": columnNumber, "错误详情：": errorObj &#125;; alert(JSON.stringify(rst, null, 10)); &#125;);&#125;; 如何通过 js 修改微信浏览器的title? 1234567var $body = $('body');document.title = 'title'; // hack在微信等webview中无法修改document.title的情况 var $iframe = $('&lt;iframe src="/favicon.ico"&gt;&lt;/iframe&gt;').on('load', function()&#123; setTimeout(function()&#123; $iframe.off('load').remove() &#125;, 0) &#125;).appendTo($body) 1. 常用方法 - js 字符串长度截取 1234567891011121314151617181920function cutstr(str, len) &#123; var temp, icount = 0, patrn = /[^\x00-\xff]/， strre = ""; for (var i = 0; i &lt; str.length; i++) &#123; if (icount &lt; len - 1) &#123; temp = str.substr(i, 1); if (patrn.exec(temp) == null) &#123; icount = icount + 1 &#125; else &#123; icount = icount + 2 &#125; strre += temp &#125; else &#123; break; &#125; &#125; return strre + "..."&#125; 替换全部 1234String.prototype.replaceAll = function(s1, s2) &#123; return this.replace(new RegExp(s1, "gm"), s2)&#125;` 清除空格 1234String.prototype.trim = function() &#123; var reExtraSpace = /^\s*(.*?)\s+$/; return this.replace(reExtraSpace, "$1")&#125; 清除左空格/右空格 12function ltrim(s)&#123; return s.replace( /^(\s*| *)/, ""); &#125; function rtrim(s)&#123; return s.replace( /(\s*| *)$/, ""); &#125; 判断是否以某个字符串开头 123String.prototype.startWith = function (s) &#123; return this.indexOf(s) == 0&#125; 判断是否以某个字符串结束 1234String.prototype.endWith = function (s) &#123; var d = this.length - s.length; return (d &gt;= 0 &amp;&amp; this.lastIndexOf(s) == d)&#125; 转义html标签 123function HtmlEncode(text) &#123; return text.replace(/&amp;/g, '&amp;').replace(/"/g, '"').replace(/&lt;/g, '&lt;').replace(/&gt;/g, '&gt;')&#125; 时间日期格式转换 123456789101112131415161718Date.prototype.Format = function(formatStr) &#123; var str = formatStr; var Week = ['日', '一', '二', '三', '四', '五', '六']; str = str.replace(/yyyy|YYYY/, this.getFullYear()); str = str.replace(/yy|YY/, (this.getYear() % 100) &gt; 9 ? (this.getYear() % 100).toString() : '0' + (this.getYear() % 100)); str = str.replace(/MM/, (this.getMonth() + 1) &gt; 9 ? (this.getMonth() + 1).toString() : '0' + (this.getMonth() + 1)); str = str.replace(/M/g, (this.getMonth() + 1)); str = str.replace(/w|W/g, Week[this.getDay()]); str = str.replace(/dd|DD/, this.getDate() &gt; 9 ? this.getDate().toString() : '0' + this.getDate()); str = str.replace(/d|D/g, this.getDate()); str = str.replace(/hh|HH/, this.getHours() &gt; 9 ? this.getHours().toString() : '0' + this.getHours()); str = str.replace(/h|H/g, this.getHours()); str = str.replace(/mm/, this.getMinutes() &gt; 9 ? this.getMinutes().toString() : '0' + this.getMinutes()); str = str.replace(/m/g, this.getMinutes()); str = str.replace(/ss|SS/, this.getSeconds() &gt; 9 ? this.getSeconds().toString() : '0' + this.getSeconds()); str = str.replace(/s|S/g, this.getSeconds()); return str&#125; 判断是否为数字类型 12345678function isDigit(value) &#123; var patrn = /^[0-9]*$/; if (patrn.exec(value) == null || value == "") &#123; return false &#125; else &#123; return true &#125;&#125; 设置cookie值 123456789function setCookie(name, value, Hours) &#123; var d = new Date(); var offset = 8; var utc = d.getTime() + (d.getTimezoneOffset() * 60000); var nd = utc + (3600000 * offset); var exp = new Date(nd); exp.setTime(exp.getTime() + Hours * 60 * 60 * 1000); document.cookie = name + "=" + escape(value) + ";path=/;expires=" + exp.toGMTString() + ";domain=360doc.com;"&#125; 获取cookie值 12345function getCookie(name) &#123; var arr = document.cookie.match(new RegExp("(^| )" + name + "=([^;]*)(;|$)")); if (arr != null) return unescape(arr[2]); return null&#125; 加载样式文件表 123456789101112function LoadStyle(url) &#123; try &#123; document.createStyleSheet(url) &#125; catch(e) &#123; var cssLink = document.createElement('link'); cssLink.rel = 'stylesheet'; cssLink.type = 'text/css'; cssLink.href = url; var head = document.getElementsByTagName('head')[0]; head.appendChild(cssLink) &#125;&#125; 返回脚本内容 123456789101112131415161718function evalscript(s) &#123; if(s.indexOf(