123; xmlhttp.Send(); &#125;catch(e)&#123; &#125;finally&#123; var result = xmlhttp.responseText; if(result)&#123; if(xmlhttp.Status==200)&#123; return(true); &#125;else&#123; return(false); &#125; &#125;else&#123; return(false); &#125; &#125;&#125; 获取当前路径 123456var currentPageUrl = "";if (typeof this.href === "undefined") &#123; currentPageUrl = document.location.toString().toLowerCase();&#125;else &#123; currentPageUrl = this.href.toString().toLowerCase();&#125; 获取页面高度 123456function getPageHeight()&#123; var g = document, a = g.body, f = g.documentElement, d = g.compatMode == "BackCompat" ? a : g.documentElement; return Math.max(f.scrollHeight, a.scrollHeight, d.clientHeight);&#125; 获取页面可视宽度 12345function getPageViewWidth()&#123; var d = document, a = d.compatMode == "BackCompat" ? d.body: d.documentElement; return a.clientWidth;&#125; 获取页面宽度 12345function getPageWidth()&#123; var g = document, a = g.body, f = g.documentElement, d = g.compatMode == "BackCompat"? a: g.documentElement; return Math.max(f.scrollWidth, a.scrollWidth, d.clientWidth);&#125; 随机数时间戳 1234function uniqueId()&#123; var a=Math.random,b=parseInt; return Number(new Date()).toString()+b(10*a())+b(10*a())+b(10*a());&#125; 日期格式化函数 1234567891011121314151617181920Date.prototype.format = function(format)&#123; var o = &#123; "M+" : this.getMonth()+1, //month "d+" : this.getDate(), //day "h+" : this.getHours(), //hour "m+" : this.getMinutes(), //minute "s+" : this.getSeconds(), //second "q+" : Math.floor((this.getMonth()+3)/3), //quarter "S" : this.getMilliseconds() //millisecond &#125;; if(/(y+)/.test(format)) format=format.replace(RegExp.$1,(this.getFullYear()+"").substr(4 - RegExp.$1.length)); for(var k in o)&#123; if(new RegExp("("+ k +")").test(format)) format = format.replace(RegExp.$1,RegExp.$1.length==1 ? o[k] :("00"+ o[k]).substr((""+ o[k]).length)); &#125; return format;&#125;//调用//new Date().format("yyyy-MM-dd hh:mm:ss"); 返回顶部的通用方法 1234567891011121314151617181920function backTop(btnId) &#123